sys_script_include.script:
function ChatGPT(strPrompt)
{
	if (IsActive() == false)
	{
		return false;
	}

	var HttpRequest = x_snc_devtools.HttpRequest;
	var ParseJson = x_snc_devtools.ParseJson;
	var GetValue = x_snc_devtools.GetValue;

	var strApiKey = GetApiKey();

	var request = {};
	request.url = 'https://api.openai.com/v1/completions';
	request.headers = [
			{ name: 'Content-Type', value: 'application/json' },
			{ name: 'Authorization', value: 'Bearer '+strApiKey }
		];
	request.requestbody = '{"model": "text-davinci-003", "prompt": "'+strPrompt+'", "temperature": 0, "max_tokens": 2048 }';

	var strResponseBody = HttpRequest(request);

	var response = ParseJson(strResponseBody)

	var strAnswer = GetValue(response,'choices',0,'text');

	//Log(strResponseBody);
	//Log(x_snc_devtools.RenderValue(response));


	return strAnswer;
}
