sys_script_include.script:
function PromptCodeReview(strSourceCode)
{
	if (IsActive() == false)
	{
		return false;
	}

	var GetStringValue = x_snc_devtools.GetStringValue;
	var RenderValue = x_snc_devtools.RenderValue;

	strSourceCode = GetStringValue(strSourceCode);

	if (strSourceCode == '')
	{
		return false;
	}

	/*
	if (strFallback == '')
	{
		strFallback = 'I don\'t know';
	}
	*/

	var strPrompt = '';


	strPrompt += 'Find defects, optimize or complete the provided source code.\n';
	strPrompt += 'Ignore undefined functions. Assume undefined functions are defined elsewhere.\n';
	//strPrompt += 'If the answer is not contained within the text below, say "'+strFallback+'"!\n';
	//strPrompt += 'If you do not understand the question, say "'+strInvalidQuestion+'"!\n';
	//strPrompt += 'Answer the question in the language of the question!\n';
	strPrompt += 'Source Code:\n';
	strPrompt += strSourceCode+'\n';

	var strAnswer = Prompt(strPrompt);

	if (strAnswer == '')
	{
		return false;
	}
	return strAnswer;
}
