sys_script_include.script:
function PromptQuestionAboutText(strText, strQuestion)
{
	if (IsActive() == false)
	{
		return false;
	}

	var GetStringValue = x_snc_devtools.GetStringValue;
	var RenderValue = x_snc_devtools.RenderValue;

	var strText = GetStringValue(strText);
	var strQuestion = GetStringValue(strQuestion);

	var strPrompt = '';
	strPrompt += 'Answer the question as truthfully as possible using the provided text, and if the answer is not contained within the text below, say "I dont know"\n';
	strPrompt += 'Context:\n';
	strPrompt += strText+'\n';
	strPrompt += 'Q: '+strQuestion;

	var strAnswer = Prompt(strPrompt);

	if (strAnswer == '')
	{
		return false;
	}
	return strAnswer;
}
